export interface Node {
  key: string | number;
  name: string;
  group?: string | number;
  icon?: string;
  descriptions?: string[];
  alerts?: {
    name: string;
    value: number;
  }[];
  x?: number;
  y?: number;
}

export interface Data {
  alerts: {
    name: string;
  }[];
  nodes: Node[];
  links: {
    source: string | number;
    target: string | number;
    data?: {
      name: string;
      shape?: string; 
    };
  }[];
}

export interface Props {
  element: HTMLElement;
  data: Data;
  height?: number;
  grouping?: string;

  zoom?: number;
  maxZoom?: number;
  minZoom?: number;

  alerts?: boolean;
  draggable?: boolean;
  simulation?: boolean;

  onSelect?: (node: Node[]) => void;
  onMove?: (node: Node) => void;
}

